            <!-- Table to display event details -->
            <table id=\"eventTable\" class=\"table\">
                <thead>
                    <tr>
                        <th>NomEvent</th>
                        <th>Description</th>
                        <th>DateEvent</th>
                        <th>HeureDeb</th>
                        <th>Prix</th>
                        <th>Nbrp</th>
                        <th>Lieu</th>
                        <th>ImageEvent</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody id=\"eventTableBody\">
                    {% for evenement in evenements %}
                        <tr>
                            <td>{{ evenement.nomEvent }}</td>
                            <td>{{ evenement.description }}</td>
                            <td>{{ evenement.dateEvent ? evenement.dateEvent|date('Y-m-d') : '' }}</td>
                            <td>{{ evenement.heureDeb ? evenement.heureDeb|date('H:i:s') : '' }}</td>
                            <td>{{ evenement.prix }}</td>
                            <td>{{ evenement.nbrp }}</td>
                            <td>{{ evenement.lieu }}</td>
                            <td>{{ evenement.imageEvent }}</td>
                            <td>
                                <a href=\"{{ path('app_evenement_show', {'idEvent': evenement.idEvent}) }}\">Show</a>
                                <a href=\"{{ path('app_evenement_edit', {'idEvent': evenement.idEvent}) }}\">Edit</a>
                            </td>
                        </tr>
                    {% else %}
                        <tr>
                            <td colspan=\"9\">No records found</td>
                        </tr>
                    {% endfor %}
                </tbody>
            </table>

            <a href=\"{{ path('app_evenement_new') }}\">Create new</a>
