       <script>
    document.addEventListener('DOMContentLoaded', function() {
        


       

        // Live search functionality
        var searchInput = document.getElementById('searchInput');
        if (searchInput) {
            searchInput.addEventListener('input', function() {
                var searchText = this.value.trim();

                // Perform AJAX request to fetch filtered events
                fetch(\"{{ path('app_evenement_search') }}?search=\" + searchText)
                    .then(function(response) {
                        return response.json();
                    })
                    .then(function(data) {
                        var eventTableBody = document.getElementById('eventTableBody');
                        eventTableBody.innerHTML = ''; // Clear existing table rows

                        // Populate table with filtered event data
                        data.evenements.forEach(function(evenement) {
                            var row = '<tr>' +
                                '<td>' + evenement.nomEvent + '</td>' +
                                '<td>' + evenement.description + '</td>' +
                                '<td>' + (evenement.dateEvent ? evenement.dateEvent : '') + '</td>' +
                                '<td>' + (evenement.heureDeb ? evenement.heureDeb : '') + '</td>' +
                                '<td>' + evenement.prix + '</td>' +
                                '<td>' + evenement.nbrp + '</td>' +
                                '<td>' + evenement.lieu + '</td>' +
                                '<td>' + evenement.imageEvent + '</td>' +
                                '<td>' +
                                    '<a href=\"{{ path('app_evenement_show', {'idEvent': ' + evenement.idEvent + '}) }}\">Show</a>' +
                                    '<a href=\"{{ path('app_evenement_edit', {'idEvent': ' + evenement.idEvent + '}) }}\">Edit</a>' +
                                '</td>' +
                            '</tr>';
                            eventTableBody.innerHTML += row;
                        });
                    })
                    .catch(function(error) {
                        console.error('Error fetching filtered events:', error);
                    });
            });
        }
    });
</script>

